import React, { useState } from 'react';

function ContactPage() {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [submittedData, setSubmittedData] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmittedData(formData);
    setFormData({ name: '', email: '', message: '' });
  };

  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className="ContactPage-main">
      <div className="ContactPage-form-container">
        <h2>Send a Message</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
          <br />
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
          <br />
          <label htmlFor="message">Message:</label>
          <textarea
            id="message"
            name="message"
            value={formData.message}
            onChange={handleChange}
          ></textarea>
          <br />
          <button type="submit">Submit</button>
        </form>
        <div className="submitted-data-container">
          {submittedData && (
            <div className="submitted-data">
              <p>Message Sent!</p>
              <p>Name: {submittedData.name}</p>
              <p>Email: {submittedData.email}</p>
              <p>Message: {submittedData.message}</p>
            </div>
          )}
        </div>
      </div>
      <div className="ContactPage-modal-container">
        <button className='book-a-call-btn' onClick={toggleModal}>Book a Call</button>
      </div>
      {isModalOpen && (
        <div className="Modal">
          <div className="Modal-content">
            <button className="Modal-close" onClick={toggleModal}>
              Close
            </button>
            <h2>Book a Call</h2>
            <p>Here's some information about booking a call.</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default ContactPage;
