import React, { useState } from 'react';
import { BiXCircle } from "react-icons/bi";

function ContactPage() {
  // States
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [submittedData, setSubmittedData] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form data:', formData);
    setSubmittedData(formData);
    setFormData({ name: '', email: '', message: '' });
  };

  // Toggle modal open/closed state
  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className="ContactPage-main">
      <div className="ContactPage-form-container">
        <div className='form-card'>
          <h2>Send a Message</h2>
          <form onSubmit={handleSubmit}>
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
            />
            <br/>
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
            <br />
            <label htmlFor="message">Message:</label>
            <textarea
              id="message"
              name="message"
              value={formData.message}
              onChange={handleChange}
            ></textarea>
            <br />
            <button type="submit">Send</button>
          </form>
        </div>
        {/* Submitted Data */}
        {submittedData && (
          <div className="submitted-data">
            <p>Message Sent!</p>
            <p>Name: {submittedData.name}</p>
            <p>Email: {submittedData.email}</p>
            <p>Message: {submittedData.message}</p>
          </div>
        )}
      </div>

      {/* Book a Call Button */}
      <div className="ContactPage-modal-container">
        <button className='book-a-call-btn' onClick={toggleModal}>Book a Call</button>
      </div>
      
      {/* Modal */}
      {isModalOpen && (
        <div className="Modal">
          <div className="Modal-content">
            <button className="Modal-close" onClick={toggleModal}>
              <BiXCircle />
            </button>
            <h2>Book a Call</h2>
            <p>Here's some information about booking a call.</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default ContactPage;
