import React, { useState } from 'react';
import axios from 'axios';
import { BiXCircle } from "react-icons/bi";

function ContactPage() {
  // Define the state for form data and submitted data
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [submittedData, setSubmittedData] = useState(null);

  // Handle input changes and update the formData state
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission, update submittedData, and reset formData
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/contact', formData);
      console.log(response.data);
      setSubmittedData(formData);
      setFormData({ name: '', email: '', message: '' });
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  // Define the state for the modal
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Toggle the modal open/closed state
  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className="ContactPage-main">
      <div className="ContactPage-form-container">
        <div className='form-card animate__animated animate__fadeIn'>
        <h2>Send a Message</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
          <br />
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
          <br />
          <label htmlFor="message">Message:</label>
          <textarea
            id="message"
            name="message"
            value={formData.message}
            onChange={handleChange}
          ></textarea>
          <br />
          <button type="submit">Send</button>
        </form>
        </div>
        <div className="submitted-data-container">
          {submittedData && (
            <div className="submitted-data">
              <p>Message Sent!</p>
              <p>Name: {submittedData.name}</p>
              <p>Email: {submittedData.email}</p>
              <p>Message: {submittedData.message}</p>
            </div>
          )}
        </div>
      </div>

  <div className="ContactPage-modal-container animate__animated animate__fadeIn">
    <button className='book-a-call-btn' onClick={toggleModal}>Book a Call</button>
  </div>
      
  {isModalOpen && (
        <div className="Modal">
          <div className="Modal-content">
            <button className="Modal-close" onClick={toggleModal}>
            <BiXCircle />
            </button>
            <h2>Book a Call</h2>
            <p>Here's some information about booking a call.</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default ContactPage;
