import React, { useRef } from 'react';

function HoverPopupImage({ imageSrc, altText, popupText }) {
  const textWindowRef = useRef();

  const handleMouseEnter = (e) => {
    textWindowRef.current.style.display = 'block';
  };

  const handleMouseMove = (e) => {
    textWindowRef.current.style.left = e.pageX + 10 + 'px';
    textWindowRef.current.style.top = e.pageY + 10 + 'px';
  };

  const handleMouseLeave = () => {
    textWindowRef.current.style.display = 'none';
  };

  return (
    <div className='AboutPage-img-container2'
         onMouseEnter={handleMouseEnter}
         onMouseMove={handleMouseMove}
         onMouseLeave={handleMouseLeave}>
      <img src={imageSrc} alt={altText} />
      <div ref={textWindowRef} className="text-window">
        {popupText}
      </div>
    </div>
  );
}

export default HoverPopupImage;
